import React, { useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { CompanyContext } from '../context/CompanyContext';
import AuthContext from '../context/AuthContext'; // Import the AuthContext
import Modal from '../components/Modal'; // Import the Modal component
import './ClientDetail.css';
import play from '../assets/play.png';
import nope from '../assets/nope.png';

const ClientDetail = () => {
  const { companyId } = useParams();
  const { COMPANY_MAPPING, entriesLast24Hours, gravityFormEntries, allCalls, totalCalls, callsLast24Hours, trend30Days, trend24Hours } = useContext(CompanyContext);
  const { userRole } = useContext(AuthContext); // Get userRole from AuthContext
  const [filter, setFilter] = useState('all');
  const [expandedEntries, setExpandedEntries] = useState([]);
  const [showMoreCalls, setShowMoreCalls] = useState(false);
  const [showMoreEntries, setShowMoreEntries] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalContent, setModalContent] = useState(null);

  const openModal = (content) => {
    setModalContent(content);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setModalContent(null);
  };

  // Handle filtering of calls based on the selected filter
  const getFilteredCalls = () => {
    let clientCalls = allCalls[companyId] || [];

    switch (filter) {
      case 'over2minutes':
        return clientCalls.filter(call => call.duration > 120);
      case 'repeat':
        const callMap = new Map();
        return clientCalls.filter(call => {
          if (callMap.has(call.customer_phone_number)) {
            return true;
          } else {
            callMap.set(call.customer_phone_number, true);
            return false;
          }
        });
      case 'unanswered':
        return clientCalls.filter(call => !call.answered);
      default:
        return clientCalls;
    }
  };

  const filteredCalls = getFilteredCalls();

  const handleFilterChange = (selectedFilter) => {
    setFilter(selectedFilter);
  };

  const handleShowMoreCalls = () => {
    setShowMoreCalls(!showMoreCalls);
  };

  const handleShowMoreEntries = () => {
    setShowMoreEntries(!showMoreEntries);
  };

  const handleToggleExpand = (id) => {
    setExpandedEntries((prevExpandedEntries) =>
      prevExpandedEntries.includes(id)
        ? prevExpandedEntries.filter((entryId) => entryId !== id)
        : [...prevExpandedEntries, id]
    );
  };

  const formatDuration = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  if (!COMPANY_MAPPING[companyId]) {
    return <p>Company not found.</p>;
  }

  return (
    <div className="client-detail-container">
      <h1>{COMPANY_MAPPING[companyId]} Lead Hub</h1>
      <div className="stats">
        <p className="call-label">
          New Forms:
          <span className={`number ${entriesLast24Hours?.[companyId] > 0 ? 'trend-up' : ''}`}>
            {entriesLast24Hours?.[companyId] || 0}
          </span>
        </p>
        <p className="call-label">
          Total Calls:
          <span className={`number ${userRole === 'admin' ? trend30Days[companyId] : ''}`}>
            {totalCalls[companyId] || 0}
          </span>
        </p>
        <p className="call-label">
          New Calls:
          <span className={`number ${userRole === 'admin' ? trend24Hours[companyId] : ''}`}>
            {callsLast24Hours[companyId] || 0}
          </span>
        </p>
      </div>

      <h2>Call Rail </h2>

      <div className="filter-buttons">
        <button onClick={() => handleFilterChange('all')}>All Calls</button>
        <button onClick={() => handleFilterChange('over2minutes')}>Calls Over 2 Minutes</button>
        <button onClick={() => handleFilterChange('repeat')}>Repeat Calls</button>
        <button onClick={() => handleFilterChange('unanswered')}>Unanswered Calls</button>
      </div>
      {filteredCalls.length > 0 ? (
        <table className="client-detail-table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Number</th>
              <th>Duration</th>
              <th>Listen to Call</th>
              <th>Date & Time</th>
            </tr>
          </thead>
          <tbody>
            {filteredCalls.slice(0, showMoreCalls ? filteredCalls.length : 5).map((call) => (
              <tr key={call.call_id}>
                <td>{call.customer_name}</td>
                <td>
                  {call.customer_phone_number ? (
                    <a className='phone' href={`tel:${call.customer_phone_number}`}>
                      {`(${call.customer_phone_number.slice(2, 5)}) ${call.customer_phone_number.slice(5, 8)}-${call.customer_phone_number.slice(8)}`}
                    </a>
                  ) : (
                    'N/A'
                  )}
                </td>
                <td>{formatDuration(call.duration)}</td>
                <td style={{ borderBottom: '1px solid #ddd', padding: '8px' }}>
                  {call.recording_player ? (
                    <a href={call.recording_player} target="_blank" rel="noopener noreferrer">
                      <img src={play} alt="Play Recording" width="20" height="20" />
                    </a>
                  ) : (
                    <img src={nope} alt="No Recording" width="20" height="20" />
                  )}
                </td>
                <td>{new Date(call.start_time).toLocaleString([], { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit' })}</td>
              </tr>
            ))}
          </tbody>
          {filteredCalls.length > 5 && (
            <div className="show-more-container">
              <button 
                className="show-more-button" 
                onClick={() => openModal(filteredCalls)}
              >
                Show More
              </button>
            </div>
          )}
        </table>
      ) : (
        <p>No calls found for this client.</p>
      )}
      <h2>Gravity Forms Entries</h2>
      <div className="gravity-forms-data">
        {gravityFormEntries.length > 0 ? (
          <table className="gravity-forms-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Number</th>
                <th>Email</th>
                <th>Message</th>
                <th>Date & Time</th>
              </tr>
            </thead>
            <tbody>
              {gravityFormEntries
                .filter(entry => entry.company_id === companyId)
                .slice(0, showMoreEntries ? gravityFormEntries.length : 5)
                .map((entry) => (
                  <tr key={entry.id}>
                    <td>{entry.name}</td>
                    <td>
                      {entry.phone ? (
                        <a className='phone' href={`tel:${entry.phone}`}>
                          {(() => {
                            const cleanedNumber = entry.phone.replace(/\D/g, '');
                            return `(${cleanedNumber.slice(0, 3)}) ${cleanedNumber.slice(3, 6)}-${cleanedNumber.slice(6)}`;
                          })()}
                        </a>
                      ) : (
                        'N/A'
                      )}
                    </td>
                    <td>
                      {entry.email ? (
                        <a className='link' href={`mailto:${entry.email}`}>
                          {entry.email}
                        </a>
                      ) : (
                        'N/A'
                      )}
                    </td>
                    <td>
                      {expandedEntries.includes(entry.id) ? (
                        <>
                          {entry.message}
                          <button className='show-button' onClick={() => handleToggleExpand(entry.id)}>Show Less</button>
                        </>
                      ) : (
                        <>
                          {entry.message.substring(0, 100)}...
                          <button className='show-button' onClick={() => handleToggleExpand(entry.id)}>Show More</button>
                        </>
                      )}
                    </td>
                    <td>{new Date(entry.date_created).toLocaleString([], { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit' })}</td>
                  </tr>
                ))}
            </tbody>
            {gravityFormEntries.length > 5 && (
              <div className="show-more-container">
                <button 
                  className="show-more-button" 
                  onClick={() => openModal(gravityFormEntries)}
                >
                  Show More
                </button>
              </div>
            )}
          </table>
        ) : (
          <p>No Gravity Forms entries found.</p>
        )}
      </div>
      {isModalOpen && (
  <Modal isOpen={isModalOpen} onClose={closeModal}>
    <div className="modal-table-container">
      <table className="client-detail-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Number</th>
            {/* Render appropriate headers based on data type */}
            {modalContent[0]?.duration && <th>Duration</th>}
            {(modalContent[0]?.recording_player || modalContent[0]?.duration) && <th>Listen to Call</th>}
            {modalContent[0]?.email && <th>Email</th>}
            {modalContent[0]?.message && <th>Message</th>}
            <th>Date & Time</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(modalContent) && modalContent.map((item, index) => {
            const rawPhone = item.customer_phone_number || item.phone;
            const cleanedPhone = rawPhone ? rawPhone.replace(/\D/g, '') : '';

            const formatPhoneNumber = (phone) => {
              if (phone.length === 10) {
                return `(${phone.slice(0, 3)}) ${phone.slice(3, 6)}-${phone.slice(6)}`;
              } else if (phone.length === 11 && phone.startsWith('1')) {
                return `(${phone.slice(1, 4)}) ${phone.slice(4, 7)}-${phone.slice(7)}`;
              } else {
                return rawPhone;
              }
            };

            return (
              <tr key={index}>
                <td className="truncate">{item.customer_name || item.name}</td>
                <td className="truncate">
                  {rawPhone ? (
                    <a
                      className="phone"
                      href={`tel:${cleanedPhone}`}
                    >
                      {formatPhoneNumber(cleanedPhone)}
                    </a>
                  ) : (
                    'N/A'
                  )}
                </td>
                {/* Conditionally render columns based on data type */}
                {item.duration && <td>{formatDuration(item.duration)}</td>}
                <td>
                  {item.recording_player ? (
                    <a href={item.recording_player} target="_blank" rel="noopener noreferrer">
                      <img src={play} alt="Play Recording" width="20" height="20" />
                    </a>
                  ) : (
                    <img src={nope} alt="No Recording" width="20" height="20" />
                  )}
                </td>
                {item.email && (
                  <td className="truncate">
                    <a href={`mailto:${item.email}`} className="link">
                      {item.email}
                    </a>
                  </td>
                )}
                {item.message && (
                  <td className="truncate message">
                    {expandedEntries.includes(item.id) ? (
                      <>
                        {item.message}
                        <button className="show-button" onClick={() => handleToggleExpand(item.id)}>Show Less</button>
                      </>
                    ) : (
                      <>
                        {item.message.substring(0, 100)}...
                        <button className="show-button" onClick={() => handleToggleExpand(item.id)}>Read More</button>
                      </>
                    )}
                  </td>
                )}
                <td>{new Date(item.start_time || item.date_created).toLocaleString([], { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit' })}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  </Modal>
)}


    </div>
  );
};

export default ClientDetail;
