import React, { useState, useContext } from "react";
import axios from "axios";
import AuthContext from "../context/AuthContext";
import './SignUpForm.css'; // You can style this similarly to the login form

const SignUpForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirmation, setPasswordConfirmation] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const { login } = useContext(AuthContext);

  const handleSignUp = async (e) => {
    e.preventDefault();
    setError(null); // Reset any previous error
    setLoading(true); // Start loading

    // Validate password confirmation matches password
    if (password !== passwordConfirmation) {
      setError("Passwords do not match.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post("http://localhost:3000/sign_up", {
        user: {
          email,
          password,
          password_confirmation: passwordConfirmation,
        },
      });

      const sessionToken = response.headers["x-session-token"];
      if (sessionToken) {
        login(sessionToken);
        console.log("Sign-up successful!");
      } else {
        setError("No session token returned");
      }
    } catch (err) {
      if (err.response && err.response.data) {
        if (Array.isArray(err.response.data)) {
          setError(err.response.data.join(", "));
        } else if (typeof err.response.data === "string") {
          setError(err.response.data);
        } else {
          setError("An unexpected error occurred.");
        }
      } else {
        setError("Error signing up. Please try again.");
      }
    }
    setLoading(false);
  };

  return (
    <div className="signup-container">
      <form className="signup-form" onSubmit={handleSignUp}>
        <h2>Sign Up</h2>
        {error && <p>{error}</p>}
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Confirm Password:</label>
          <input
            type="password"
            value={passwordConfirmation}
            onChange={(e) => setPasswordConfirmation(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? "Signing Up..." : "Sign Up"}
        </button>
      </form>
    </div>
  );
};

export default SignUpForm;
