export const CompanyContext = createContext();

export const CompanyProvider = ({ children }) => {
  const [selectedCompany, setSelectedCompany] = useState(null); // Default to null, handle selection properly
  const [totalCalls, setTotalCalls] = useState({});
  const [callsLast24Hours, setCallsLast24Hours] = useState({});
  const [trend24Hours, setTrend24Hours] = useState({});
  const [trend30Days, setTrend30Days] = useState({});
  const [entriesLast24Hours, setEntriesLast24Hours] = useState({});
  const [gravityFormEntries, setGravityFormEntries] = useState([]);

  // Fetch functions...

  useEffect(() => {
    if (selectedCompany) {
      fetchCallData(selectedCompany);
      fetchGravityFormsData();
    }
  }, [selectedCompany]);

  return (
    <CompanyContext.Provider value={{ 
      selectedCompany, 
      setSelectedCompany, // Ensure this is passed down
      COMPANY_MAPPING, 
      totalCalls, 
      callsLast24Hours,
      trend24Hours,
      trend30Days,
      entriesLast24Hours,
      gravityFormEntries
    }}>
      {children}
    </CompanyContext.Provider>
  );
};
