import React, { createContext, useState } from "react";

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [authToken, setAuthToken] = useState(localStorage.getItem("sessionToken") || null);
  const [userRole, setUserRole] = useState(localStorage.getItem("userRole") || null);
  const [loggedCompanyId, setLoggedCompanyId] = useState(localStorage.getItem("loggedCompanyId") || null);

  const login = (token, role, companyId) => {
    setAuthToken(token);
    setUserRole(role);
    setLoggedCompanyId(companyId);

    localStorage.setItem("sessionToken", token);
    localStorage.setItem("userRole", role);
    localStorage.setItem("loggedCompanyId", companyId);
  };

  const logout = () => {
    setAuthToken(null);
    setUserRole(null);
    setLoggedCompanyId(null);

    localStorage.removeItem("sessionToken");
    localStorage.removeItem("userRole");
    localStorage.removeItem("loggedCompanyId");
  };

  return (
    <AuthContext.Provider value={{ authToken, userRole, loggedCompanyId, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export default AuthContext;
