import React, { useContext } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import Dashboard from './components/Dashboard';
import ClientDetail from './components/ClientDetail';
import LoginForm from './components/LoginForm'; // Import the LoginForm component
import { Route, Routes, Navigate } from 'react-router-dom';
import AuthContext from './context/AuthContext'; // Import AuthContext

function App() {
  const { authToken } = useContext(AuthContext); // Get the authToken from AuthContext

  return (
    <div className="App">
      <Navbar />
      <Routes>
        {/* Conditionally render the dashboard if the user is authenticated, otherwise show the LoginForm */}
        <Route exact path="/" element={authToken ? <Dashboard /> : <LoginForm />} />
        <Route path="/client/:companyId" element={authToken ? <ClientDetail /> : <Navigate to="/" />} />
      </Routes>
    </div>
  );
}

export default App;
