import React, { useContext } from 'react';
import { CompanyContext } from '../context/CompanyContext';
import ClientBox from './ClientBox';
import './Dashboard.css';

const Dashboard = () => {
  // Destructure data from CompanyContext
  const { COMPANY_MAPPING } = useContext(CompanyContext);
  const { clientCallCounts = [], showMore, setShowMore } = useContext(CompanyContext);

  // Generate client boxes even if clientCallCounts is empty
  const clientData = Object.keys(COMPANY_MAPPING).map(companyId => ({
    companyId,
    companyName: COMPANY_MAPPING[companyId],
    callCount: clientCallCounts[companyId]?.callCount || 0, // Default to 0 if no call count
  }));

  const handleShowMore = () => {
    setShowMore(!showMore);
  };

  return (
    <div className="dashboard">
      <h1>Client Performance Overview</h1>
      <div className="client-boxes">
        {clientData
          .slice(0, showMore ? clientData.length : 8) // Show all if showMore is true, else show only first 8
          .map(client => (
            <ClientBox 
              key={client.companyId} 
              companyId={client.companyId} 
              companyName={client.companyName} 
              totalCalls={client.callCount} 
            />
          ))}
      </div>
      {clientData.length > 8 && (
        <button className="show-more-button" onClick={handleShowMore}>
          {showMore ? 'Show Less' : 'Show More'}
        </button>
      )}
    </div>
  );
};

export default Dashboard;
