import React, { useState, useContext } from "react";
import axios from "axios";
import AuthContext from "../context/AuthContext";
import './SignUpForm.css'; // You can style this similarly to the login form

const SignUpForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [passwordConfirmation, setPasswordConfirmation] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const { login } = useContext(AuthContext);

  const handleLogin = async (e) => {
    e.preventDefault();
    console.log("Login form submitted");

    try {
      console.log("Sending login request with:", { email, password });

      const response = await axios.post("http://localhost:3000/sign_in", {
        email: email,
        password: password,
      });

      console.log("Login response:", response);

      const sessionToken = response.headers["x-session-token"];
      if (sessionToken) {
        login(sessionToken);
        console.log("Login successful, token:", sessionToken);
      } else {
        setError("No session token returned");
        console.error("No session token in headers");
      }
    } catch (err) {
      console.error("Error during login:", err);
      setError("Invalid email or password");
    }
  };




  return (
    <div className="signup-container">
      <form className="signup-form" onSubmit={handleSignUp}>
        <h2>Sign Up</h2>
        {error && <p>{error}</p>}
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Confirm Password:</label>
          <input
            type="password"
            value={passwordConfirmation}
            onChange={(e) => setPasswordConfirmation(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? "Signing Up..." : "Sign Up"}
        </button>
      </form>
    </div>
  );
};

export default SignUpForm;
